body:
{
	// (INT) 素材源(可以不赋值，默认 0 自动)
	"materialSource": 1,

	// (INT) 素材类型
	"materialType": 1,

	// (STRING [数组]) 关键字
	"keywords": ["", ],

	// (MESSAGE) 排序(视觉中国仅支持单独一个条件)
	"sorts": 
	{
		// (STRING) 
		"key": "",

		// (STRING) 
		"value": "",
	},

	// (MESSAGE) 过滤
	"filters": 
	{
		// (STRING) 
		"key": "",

		// (STRING) 
		"value": "",
	},

	// (INT) 页码(视觉中国[1,1000]
	"page": 1,

	// (INT) 每页数量(视觉中国[1,100])
	"nums": 1,
}

response:
{
	// (MESSAGE) 素材数据列表
	"searchResult": 
	{
		// (MESSAGE [数组]) 素材数据列表
		"data": [
		{
			// (INT) 素材类型
			// MaterialType
			// NOT_SUPPORT_MATERIAL_TYPE: 全部
			// IMAGE_TYPE: 图片
			"materialType": 1,

			// (STRING) id
			"id": "",

			// (STRING) 标题
			"title": "",

			// (STRING) 说明
			"description": "",

			// (STRING) 格式]
			"assetFormat": "",

			// (INT) 购买情况
			// PurchaseType
			// FREE: 免费
			// UNPAID: 需付费
			// PURCHASED: 已付费
			// VIP: 会员无需付费
			"purchaseType": 1,

			// (STRING) 商品code
			"productCode": "",

			// (STRING) 预览图
			"thumbnailUrl": "",

			// (INT) 可使用的等级限制
			"serviceLevel": 1,

			// (STRING) 类型数据，根据 materialType 的值决定，json 结构，1 = Image [MaterialImage]
			"content": "",

			// (INT) 素材源
			// MaterialSource
			// NONE: 无
			// VCG: 视觉中国
			"materialSource": 1,

			// (INT) 单独购买价格
			"price": 1,
		}, ],

		// (INT) 返回结果总数
		"totalCount": 1,

		// (INT) 返回结果总页数
		"totalPage": 1,

		// (INT) 每页结果总数
		"perPage": 1,

		// (INT) 当前页码
		"curPage": 1,
	},

	// (MESSAGE) 通用返回
	"commonResponse": 
	{
		// (STRING) 消息
		"msg": "",

		// (INT) 状态码
		"status": 1,

		// (ENUM) 
		// ResponseStatus
		// SUCCESS: 成功
		// INVALID_PARAM: 无效参数
		// PERMISSION_DENIED: 权限不足
		// NO_USER: 不存在的用户
		// INTERNAL: 内部错误
		// PAY_FAILED: 付款失败
		// VCG_HTTP_STATUS_FAILED: [视觉中国]请求失败
		// VCG_HTTP_STATUS_URL: [视觉中国]URL 错误
		// VCG_HTTP_STATUS_SPEED: [视觉中国]被
		"protoStatus": "INVALID_PARAM",
	},
}

